mapper.select:
SELECT * FROM ( SELECT rownum r, a.* FROM ( SELECT revno, br.beanno, br.userno, usernick, revcont, revhit, revdate, revstarpoint, beanname, cupnotename FROM beanRev br JOIN bean b ON br.beanno = b.beanno JOIN MEMBER m ON m.userno = br.userno JOIN beantaste bt ON br.beanno = bt.beanno JOIN cupnote cn ON cn.cupnoteno = bt.cupnoteno
AND upper(beanname) LIKE '%'||upper(#{search})||'%'
AND cupnotename = #{category }
ORDER BY revno DESC
ORDER BY revstarpoint DESC, revno DESC
ORDER BY revno DESC
) a )
AND r BETWEEN #{paging.startNo } AND #{paging.endNo }

SELECT count(*) FROM beanrev br JOIN bean b ON br.beanno = b.beanno JOIN MEMBER m ON m.userno = br.userno JOIN beantaste bt ON br.beanno = bt.beanno JOIN cupnote cn ON cn.cupnoteno = bt.cupnoteno
AND cupnotename = #{category }
AND upper(beanname) LIKE '%'||upper(#{search})||'%'

SELECT commno, revno, b.userno, commcont, commtag, commdate, usernick FROM beanrevcomm b JOIN MEMBER m ON b.userno = m.userno
AND revNo = #{revNo }
ORDER BY commno

SELECT businessNo FROM bean b
JOIN beanrev br ON b.beanno = br.beanno

	br.revno = #{revNo }

SELECT beanName
FROM bean

	AND beanno = #{beanNo }

mapper.update:
UPDATE BeanRev
SET revCont = #{revCont }
	, revstarpoint = #{revStarPoint }

	revNo = #{revNo }

UPDATE beanrevcomm
SET commcont = #{commCont }
WHERE commno = #{commNo }
