mapper.select:
SELECT * FROM ( SELECT rownum r, a.* FROM ( SELECT revno, cr.cafeno, cr.userno, usernick, revcontent, revhit, revdate, revsp, cafename, cafeloc FROM cafeRev cr JOIN cafe c ON cr.cafeno = c.cafeno JOIN member m ON cr.userno = m.userno
AND upper(cafename) LIKE '%'||upper(#{search})||'%'
AND cafeloc = #{category }
ORDER BY revno DESC
ORDER BY revsp DESC, revno DESC
ORDER BY revno DESC
) a )
AND r BETWEEN #{paging.startNo } AND #{paging.endNo }

SELECT caferevcommno, revno, c.userno, cafecommcont , cafecommtag , cafecommdate, usernick FROM caferevcomm c JOIN MEMBER m ON c.userno = m.userno
AND revNo = #{revNo }
ORDER BY caferevcommno

SELECT count(*) FROM FREEBOARD


		AND FREEBOARDCATEGORY = #{category}


		AND upper(FREEBOARDTITLE) LIKE '%'||upper(#{search})||'%'

SELECT FREEBOARDNO,USERNO,FREEBOARDCATEGORY,FREEBOARDTITLE,FREEBOARDCONTENT,FREEBOARDHIT,FREEBOARDWRITEDATE,FREEIMGNO  
FROM (
	SELECT rownum r,a.* FROM(
		SELECT * FROM FREEBOARD 
		WHERE upper(FREEBOARDTITLE) LIKE '%'||upper(#{search})||'%'

			AND FREEBOARDCATEGORY = #{category}

		ORDER BY FREEBOARDNO DESC
		)a
	)
WHERE r BETWEEN #{paging.startNo} AND #{paging.endNo}

SELECT count(*) FROM freeboardrecommend

	AND freeBoardNo = #{freeBoardNo}
	AND userno = #{userNo}

mapper.delete:
DELETE FROM freeboardrecommend

	AND freeBoardNo = #{freeBoardNo}
	AND userno = #{userNo}

mapper.update:
UPDATE CafeRev
SET revContent = #{revContent }
	, revsp = #{revsp }

	revNo = #{revNo }

mapper.select:
SELECT NOTICENO,TITLE,CONTENT,WIRTEDATE
FROM (
	SELECT rownum r,a.* FROM(
		SELECT * FROM NOTICE 
		WHERE upper(TITLE) LIKE '%'||upper(#{search})||'%'
		ORDER BY NOTICENO DESC
		)a
	)
WHERE r BETWEEN #{paging.startNo} AND #{paging.endNo}

SELECT count(*) FROM caferev cr JOIN cafe c ON c.cafeno = cr.cafeno
AND cafeloc = #{category }
AND upper(cafename) LIKE '%'||upper(#{search})||'%'

SELECT businessNo FROM cafe c
JOIN caferev cr ON c.cafeno = cr.cafeno

	cr.revno = #{revNo }

SELECT MYRIPNO,USERNO,MYRIPTITLE,MYRIPCONTENT,MYRIPHIT,MYRIPWRITEDATE,MYRIPIMGNO
FROM (
	SELECT rownum r,a.* FROM(
		SELECT * FROM MYRECIPE
		WHERE upper(MYRIPTITLE) LIKE '%'||upper(#{search})||'%'
		ORDER BY MYRIPNO DESC
		)a
	)
WHERE r BETWEEN #{paging.startNo} AND #{paging.endNo}

SELECT * FROM MYRECIPE m ,MYRECIPEFILE f

	AND m.MYRIPNO = f.MYRIPNO(+)
	AND m.MYRIPNO = #{myRipNo}

SELECT * FROM MEMBER m,MEMBERQUIZRESULT q

	AND m.USERNO = q.USERNO 
	AND m.USERNO = #{userNo}

mapper.update:
UPDATE MYRECIPE 
SET 
	MYRIPTITLE = #{myRipTitle},
	MYRIPCONTENT = #{myRipContent}
WHERE MYRIPNO = #{myRipNo}

mapper.select:
SELECT EVENTNO, EVEORINAME, EVESTART, EVEEND, EVEIMG
FROM EVENT

	EVENTNO = #{eventNo}

SELECT count(*) FROM MYRECIPERECOMMEND

	AND myRipNo = #{myRipNo}
	AND userNo = #{userNo}

mapper.delete:
DELETE FROM MYRECIPERECOMMEND

	AND myRipNo = #{myRipNo}
	AND userNo = #{userNo}
