mapper.select:
SELECT count(*) cnt
FROM member

	AND userId = #{userId}

SELECT count(*) cnt
FROM member

	AND userNick = #{userNick}

SELECT count(*) cnt
FROM member

	AND userId = #{userId}
	AND userPw = #{userPw}

SELECT userId FROM member

	AND userName = #{userName}		
	AND userPhone = #{userPhone}

SELECT userPw FROM member

	AND userId = #{userId}		
	AND userName = #{userName}		
	AND userPhone = #{userPhone}
