mapper.select:
SELECT C1.CAFENO, USERNO, C2.CAFENO, CAFENAME, CAFECOMM, CAFEPHONE, BUSYTIME, CAFELOC, CAFEADD1, CAFEADD2, CAFEINFO FROM CAFEWISH C1 INNER JOIN CAFE C2 ON C1.CAFENO = C2.CAFENO
USERNO = #{userNo}
AND upper(cafeName) LIKE '%'||upper(#{searchText})||'%'

mapper.update:
UPDATE MEMBER

	, userPw = #{userPw}
	, userNick = #{userNick}			
	, userEmail = #{userEmail}			
	, userPhone = #{userPhone}			
	, userPostcode = #{userPostcode}			
	, userAdd1 = #{userAdd1}			
	, userAdd2 = #{userAdd2}			


	userNo = #{userNo}

mapper.select:
SELECT count(*) cnt
FROM member

	AND userNick = #{userNick}

SELECT freeBoardNo, userNo, freeBoardCategory, freeBoardTitle, freeBoardWriteDate FROM FreeBoard
userNo = #{userNo}
ORDER BY freeBoardNo DESC

SELECT myRipNo, userNo, myRipTitle, myRipContent, myRipHit, myRipWriteDate FROM MyRecipe
userNo = #{userNo}
ORDER BY myRipNo DESC
