mapper.select:
SELECT C1.CAFENO, USERNO, C2.CAFENO, CAFENAME, CAFECOMM, CAFEPHONE, BUSYTIME, CAFELOC, CAFEADD1, CAFEADD2, CAFEINFO FROM CAFEWISH C1 INNER JOIN CAFE C2 ON C1.CAFENO = C2.CAFENO
USERNO = #{userNo}
AND upper(cafeName) LIKE '%'||upper(#{searchText})||'%'

SELECT b1.SUBNO, b1.USERNO, b1.BEANNO, b1.GRAM, b1.PRICE, b1.GRIND, b1.SUBSTARTDATE, b1.SUBENDDATE, b1.SUBTIME, b1.NONSUB, b2.BEANNAME, b3.BEANORIGINNAME FROM BEANSUB b1, BEAN b2, BEANIMG b3
AND b1.BEANNO = b2.BEANNO AND b2.BEANIMGNO = b3.BEANIMGNO AND USERNO = #{userNo}
ORDER BY SUBNO

mapper.update:
UPDATE MEMBER

	, userPw = #{userPw}
	, userNick = #{userNick}			
	, userEmail = #{userEmail}			
	, userPhone = #{userPhone}			
	, userPostcode = #{userPostcode}			
	, userAdd1 = #{userAdd1}			
	, userAdd2 = #{userAdd2}			


	userNo = #{userNo}

mapper.select:
SELECT count(*) cnt
FROM member

	AND userNick = #{userNick}

SELECT r.revNo, r.cafeNo, r.userNo, r.revContent, r.revHit, r.revDate, r.revsp , c.imgNo, c.cafeName, c.cafeComm, c.cafePhone, c.busyTime, c.cafeLoc , c.cafeAdd1, c.cafeAdd2, c.businessNo, c.cafeInfo FROM CafeRev r JOIN Cafe c ON r.cafeNo = c.cafeNo
AND userNo = #{userNo}
ORDER BY r.revDate DESC

SELECT freeBoardNo, userNo, freeBoardCategory, freeBoardTitle, freeBoardWriteDate FROM FreeBoard
userNo = #{userNo}
ORDER BY freeBoardNo DESC

SELECT myRipNo, userNo, myRipTitle, myRipContent, myRipHit, myRipWriteDate FROM MyRecipe
userNo = #{userNo}
ORDER BY myRipNo DESC
